con:configuration.scriptText:
//Endpoint Assertion
def endPointStatus = messageExchange.getEndpoint() == "http://www.dneonline.com/calculator.asmx"

if(endPointStatus == false) {
    testRunner.fail("Invalid Endpoint")
}

//OR

assert messageExchange.getEndpoint() == "http://www.dneonline.com/calculator.asmx"

//XMLHolder import
import com.eviware.soapui.support.XmlHolder

//Create XMLHolder object
def holder = new XmlHolder(messageExchange.responseContentAsXml)

//Node exists
assert holder["//SubtractResult"] != null

con:config.script:
// Create the XmlHolder object for the response of the request
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
//Get request
def reqHolder = groovyUtils.getXmlHolder( "Subtract#Request" )

//Modify the request parameters
reqHolder["//tem:intA"] = 10
reqHolder["//tem:intB"] = 20

//Update request
reqHolder.updateProperty()

//Run the test step
testRunner.runTestStepByName("Subtract")


//Get response
def resHolder = groovyUtils.getXmlHolder( "Subtract#Response" )

//Print response value
log.info "Result: ${resHolder['//ns1:SubtractResult']}"
